#include \"src/RocketSimCuda.h\"\n#include <iostream>\n\nusing namespace RocketSim;\n\nint main() {\n    std::cout << \"RocketSim CUDA Compilation Test\" << std::endl;\n    \n    // Test CUDA availability\n    if (IsCudaAvailable()) {\n        CudaDeviceInfo info = GetCudaDeviceInfo();\n        std::cout << \"CUDA Available: \" << info.name << std::endl;\n        std::cout << \"Compute Capability: \" << info.major << \".\" << info.minor << std::endl;\n        std::cout << \"Memory: \" << (info.totalMemory / 1024 / 1024) << \" MB\" << std::endl;\n    } else {\n        std::cout << \"CUDA not available - using CPU fallback\" << std::endl;\n    }\n    \n    std::cout << \"Compilation successful!\" << std::endl;\n    return 0;\n}\n"
